import React, { useState } from "react";
import "./App.css";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useNavigate
} from "react-router-dom";

import Signup from "./components/Auth/Signup";
import LawyerProfilePending from "./components/Lawyers/LawyerProfilePending";
import LawyerDashboard from "./components/Lawyers/LawyerDashboard";
import LawyerLogin from "./components/Auth/LawyerLogin";
import ClientSignup from "./components/Clients/ClientSignup";
import ClientDashboard from "./components/Clients/ClientDashboard";
import ClientLogin from "./components/Auth/ClientLogin";



/* ---------- Dropdown Data ---------- */
const CATEGORIES = [
  "Family Law", "Criminal Law", "Business Law", "Real Estate",
  "Immigration", "Employment"
];

const LOCATIONS = [
  "Lagos", "Abuja", "Port Harcourt", "Enugu", "Ibadan",
  "Kano", "Kaduna", "Benin City"
];

const LAWYERS = [
  {
    id: 1,
    name: "Aisha Bello",
    title: "Senior Litigation Counsel",
    experience: "12 yrs",
    rating: 4.9,
    price: "₦20,000/hr",
    location: "Lagos, NG",
    img: "https://via.placeholder.com/300?text=Lawyer"
  },
  {
    id: 2,
    name: "Mike Okonkwo",
    title: "Corporate & Commercial",
    experience: "8 yrs",
    rating: 4.8,
    price: "₦15,000/hr",
    location: "Abuja, NG",
    img: "https://via.placeholder.com/300?text=Lawyer"
  },
  {
    id: 3,
    name: "Sadia Ibrahim",
    title: "Family Law Specialist",
    experience: "10 yrs",
    rating: 4.7,
    price: "₦18,000/hr",
    location: "Lagos, NG",
    img: "https://via.placeholder.com/300?text=Lawyer"
  }
];

/* ---------- Header ---------- */
function Header() {
  const [showLoginModal, setShowLoginModal] = useState(false);
  const navigate = useNavigate();

  return (
    <>
      <nav
        className="navbar navbar-expand-lg"
        style={{
          backgroundColor: "var(--navy)",
          color: "white",
          padding: "1rem 0",
        }}
      >
        <div className="container">
          <Link className="navbar-brand d-flex align-items-center text-white" to="/">
            <div
              style={{
                width: 36,
                height: 36,
                background: "var(--gold)",
                borderRadius: 8,
                marginRight: 10,
              }}
            ></div>
            <span style={{ fontWeight: 700 }}>LexConnect</span>
          </Link>

          <button
            className="navbar-toggler text-white"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navMenu"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navMenu">
            <ul className="navbar-nav ms-auto align-items-lg-center">
              <li className="nav-item ms-3">
                <button
                  className="btn btn-outline-light"
